import React, { Component } from "react";

class Eachitem extends Component {
  constructor(props) {
    super(props);
    this.state={isEditing:false,item:this.props.item};
  }

  handleClick = (e) => {
    this.props.click();
  };

  handleDelete=()=>{
    this.props.del(this.props.id);
  }

  toggleForm=()=>{
    this.setState((previous)=>{isEditing:!previous.isEditing});
  }

  render() {
    return (
      <div>
        {this.state.isEditing === false ? (
          <div>
            {this.props.item.active === false ? (
              <span
                onClick={this.handleClick}
                style={{ textDecoration: "line-through" }}
              >
                {this.props.item.todo}
              </span>
            ) : (
              <span onClick={this.handleClick}>{this.props.item.todo}</span>
            )}
            <button onClick={this.toggleForm}>Edit</button>
            <button name='delete' onClick={this.handleDelete}>Delete</button>
          </div>
        ) : (
          <div>
            <form>
              <input type="text" />
              <button>Save</button>
            </form>
          </div>
        )}
      </div>
    );
  }
}

export default Eachitem;
